/***********************************
*マップ表示
***********************************/
void DrawMap() {

	//マップ表示
	for (int y = 0; y < MAPHEIGHT; y++) {
		for (int x = 0; x < MAPWIDTH; x++) {
			if (g_StageData[0][y][x] == 0 || g_StageData[0][y][x] == 2
				|| g_StageData[0][y][x] == 3) {
				DrawBox((40 * x) + msx, 40 * y, (40 * x) + 40 + msx, 40 * y + 40, 0x22aa33, TRUE);
			}
			if (g_StageData[0][y][x] == 1) {
				DrawBox((40 * x) + msx, 40 * y, (40 * x) + 40 + msx, 40 * y + 40, 0x2222ff, TRUE);
			}
		}
	}

	for (int y = 0; y < MAPHEIGHT; y++) {
		for (int x = 0; x < MAPWIDTH; x++) {
			DrawFormatString(100 + 30 * x, 50 + 30 * y, 0xffffff, "%d", g_StageData[0][y][x]);
		}
	}
	//DrawFormatString(50,100, 0xffffff, "%d", player.px);

	//DrawBox(40, py - 40, 40 + 40, py + 40, 0xffffff, TRUE);

	DrawBox((Enemy.x - Enemy.Move + msx), (Enemy.y),
		(Enemy.x + Enemy.size - Enemy.Move + msx), (Enemy.y + Enemy.size), 0x00ffff, TRUE);//敵の描画
}